mapper.select:
SELECT spll.need_by_date, spll.promise_delivery_date, srtl.rcv_trx_line_id, srtl.rcv_trx_header_id, sph.display_po_num, sph.po_num, concat(srto.source_header_num,IFNULL(srtl.item_name,'0'),IFNULL(srto.from_display_po_num,'0'),IFNULL(srto.from_pc_num,'0')) as all_source, concat(srto.source_header_num,IFNULL(srth.trx_num,'0'),IFNULL(srto.from_display_po_num,'0')) as all_source_header, srtl.tenant_id, (case when srrsm.initial_node_flag = 1 then srto.source_header_num else srth.trx_num end ) trx_num, (case when srrsm.initial_node_flag = 1 then srto.source_header_num else srth.display_trx_num end ) display_trx_num, (case when srrsm.initial_node_flag = 1 then srto.source_line_num else srtl.trx_line_num end ) trx_line_num, sah.asn_type_code, (case when srrsm.initial_node_flag = 1 then srrsm.order_type_code else srnc2.node_config_name end ) order_type_code, (case when srrsm.initial_node_flag = 1 and srrsm.order_type_code = 'ASN' then srto.from_display_po_num else srto.source_header_num end ) source_header_num, (case when srrsm.initial_node_flag = 1 and srrsm.order_type_code = 'ASN' then srto.from_display_po_line_num else srto.source_line_num end ) source_line_num, srtl.item_id, srtl.item_code, srtl.item_name, ( srtl.quantity - srtl.occupied_quantity + srtl.reversed_quantity) quantity, srtl.quantity org_quantity, ( srtl.quantity - srtl.occupied_quantity + srtl.reversed_quantity) left_quantity, /*当前执行金额*/ ( srtl.tax_included_amount - srtl.occupied_tax_amount + srtl.reversed_tax_amount) tax_included_amount, /*不含税单价*/ srtl.net_price, /*可前执行金额*/ srtl.po_unit_price, srtl.tax_included_price, srtl.occupied_quantity, srtl.reversed_quantity, srtl.occupied_tax_amount, srtl.reversed_tax_amount, ( srtl.tax_included_amount - srtl.occupied_tax_amount + srtl.reversed_tax_amount) left_tax_amount, /*实际操作日期*/ srtl.trx_date, /*库房*/ hi.inventory_name, /*库位*/ hl.location_name, /*收货组织*/ hio.organization_name, /*单据数量*/ NULL source_quantity, /*单位*/ CONCAT( CONCAT( su.uom_code, '/' ), sut.uom_name ) uom_name, /*商品编码*/ spl.product_num, spl.product_name, /*妥投时间*/ sal.deliver_time due_date, /*供应商*/ srth.supplier_id, (case when srth.supplier_num is null then hcc.company_num else srth.supplier_num end) supplier_num, (case when srth.supplier_name is null then srth.supplier_company_name else srth.supplier_name end) supplier_name, srth.supplier_company_id, /*业务实体*/ hou.ou_name , hc.company_id, hc.company_num, hc.company_name, srto.from_po_header_id, srto.from_po_line_id, srto.from_po_line_location_id, srto.from_display_po_num, srto.from_display_po_line_num, srto.from_display_po_line_location_num, srto.from_asn_header_id, srto.from_asn_line_id, srto.from_display_asn_num, srto.from_display_asn_line_num, srto.from_pc_header_id, srto.from_pc_subject_id, srto.from_pc_num, srto.from_pc_subject_num, srsl.strategy_header_id, srrsm.initial_node_flag, srrsm.strategy_line_id, srrsm.next_strategy_line_id, srsl.subject_type, srtl.inventory_id, srtl.locator_id, srsl.line_seq, srsl.node_config_id, srsl.strategy_line_id srsl_strategy_line_id, /*节点index*/ srnc.node_config_index, /*节点编码*/ srnc.node_config_code, /*节点名称*/ srnc.node_config_name, srtl.object_version_number, srem.mapping_id as rcv_trx_type_id, srem.rcv_type_code, srem.rcv_type_name, srto.order_link_id, srtl.tax_rate, concat(srsh.strategy_code, '-', srsh.strategy_name) strategy_code, srth.rcv_status_code, srth.created_by, srtl.currency_code, (case when spl.unit_price_batch is null then 1 else spl.unit_price_batch end) unit_price_batch, srtl.move_reason, srto.from_pc_stage_id, srpd.stage_code, srpd.stage_name, srpd.check_type check_type_code, srpd.pay_ratio, srpd.pc_status_code, srpd.specifications, srpd.model, srpd.accepter_user_id, srpd.accepter_user_name, srtl.category_id, sic.category_name, sah.carriers_code, sah.carriers_name, spl.pr_header_id, spl.pr_line_id, sah.carriers_name, srtl.agent_id, hpa.purchase_agent_name, /* 添加订单状态 */ sah.asn_status FROM sinv_rcv_trx_line srtl JOIN sinv_rcv_trx_order_link srto ON srtl.order_link_id = srto.order_link_id JOIN sinv_rcv_trx_header srth ON srtl.rcv_trx_header_id = srth.rcv_trx_header_id JOIN sinv_rcv_record_strategy_mapping srrsm ON srth.rcv_trx_header_id = srrsm.rcv_trx_header_id LEFT JOIN sinv_rcv_strategy_line srsl ON srrsm.next_strategy_line_id = srsl.strategy_line_id LEFT JOIN sinv_rcv_strategy_header srsh ON srsl.strategy_header_id = srsh.node_strategy_id LEFT JOIN sinv_rcv_node_config srnc ON srsl.node_config_id = srnc.node_config_id LEFT JOIN hpfm_inventory hi ON srtl.inventory_id = hi.inventory_id LEFT JOIN hpfm_location hl ON srtl.locator_id = hl.location_id LEFT JOIN hpfm_inv_organization hio ON srtl.inv_organization_id = hio.organization_id LEFT JOIN smdm_uom su ON srtl.uom_id = su.uom_id LEFT JOIN smdm_uom_tl sut ON su.uom_id = sut.uom_id AND sut.lang = #{lang} LEFT JOIN hpfm_operation_unit hou ON srtl.ou_id = hou.ou_id LEFT JOIN hpfm_company hc ON srth.company_id = hc.company_id LEFT JOIN hpfm_company hcc ON srth.supplier_company_id = hcc.company_id LEFT JOIN sinv_rcv_ext_mapping srem ON srtl.rcv_trx_type_id = srem.mapping_id LEFT JOIN sinv_rcv_trx_line srtl2 on srtl.from_rcv_trx_line_id = srtl2.rcv_trx_line_id LEFT JOIN sinv_asn_line sal on srto.from_asn_line_id = sal.asn_line_id LEFT JOIN sinv_asn_header sah on sal.asn_header_id = sah.asn_header_id LEFT JOIN sinv_rcv_strategy_line srsl2 ON srrsm.strategy_line_id = srsl2.strategy_line_id LEFT JOIN sinv_rcv_node_config srnc2 ON srsl2.node_config_id = srnc2.node_config_id LEFT JOIN sinv_rcv_pc_details srpd ON srto.order_link_id = srpd.order_link_id LEFT JOIN sodr_po_line_location spll on srto.from_po_line_location_id = spll.po_line_location_id LEFT JOIN sodr_po_line spl ON srto.from_po_line_id = spl.po_line_id LEFT JOIN sodr_po_header sph ON sph.po_header_id = spl.po_header_id left join sprm_pr_header srh on srh.pr_header_id=srtl.pr_header_id LEFT JOIN hpfm_purchase_agent hpa ON srtl.agent_id = hpa.purchase_agent_id left join smdm_item_category sic on sic.category_id = srtl.category_id WHERE srtl.tenant_id = #{sinvRcvTrxQueryDTO.tenantId}
AND srrsm.order_type_code = 'ASN' AND srto.from_asn_header_id in
#{item}
/*未执行完全的事务行*/ AND srtl.complete_flag = 0 /*屏蔽不是srm系统内操作的*/ and srsl.srm_enable = 1 and srth.source_code = 'SRM' AND srtl.delete_flag = 0 /*不为变更中的订单*/ AND (sph.changing_flag != 1 or srrsm.order_type_code = 'PC') /*对应的上一个来源事务应该是已完成状态-或者没有上一个事务：即初始节点*/ AND (srrsm.initial_node_flag =1 or EXISTS ( SELECT 1 FROM sinv_rcv_trx_line srtl1 WHERE srtl1.rcv_trx_line_id = srtl.from_rcv_trx_line_id AND srth.rcv_status_code = '40_FINISHED' ))
AND EXISTS ( SELECT 1 FROM sinv_rcv_strategy_permission srsp WHERE srsp.strategy_line_id = srsl.strategy_line_id AND srsp.strategy_permission_type in ('UPDATE','QUERY') AND srsp.role_id = #{customUserDetails.roleId})
AND srsl.node_config_id = #{sinvRcvTrxQueryDTO.nodeConfigId}
and concat(srto.source_header_num,IFNULL(srth.trx_num,'0'),IFNULL(srto.from_display_po_num,'0')) like #{trxNumLike}
and (case when srrsm.initial_node_flag = 1 then srto.source_header_num else srth.display_trx_num end ) like #{displayTrxNumLike}
and (case when srrsm.initial_node_flag = 1 and srrsm.order_type_code = 'ASN' then srto.from_display_po_num else srto.source_header_num end ) like #{sourceHeaderNumLike}
AND (case when srrsm.initial_node_flag = 1 and srrsm.order_type_code = 'ASN' then srto.from_display_po_line_num else srto.source_line_num end ) like #{sourceLineNumLike}
and srtl.item_id = #{sinvRcvTrxQueryDTO.itemId}
AND srtl.supplier_company_id = #{sinvRcvTrxQueryDTO.supplierCompanyId}
AND srem.mapping_id = #{sinvRcvTrxQueryDTO.rcvTrxTypeId}
AND concat(srto.source_header_num,IFNULL(srtl.item_name,'0'),IFNULL(srto.from_display_po_num,'0'),IFNULL(srto.from_pc_num,'0')) like #{allSourceLike}
AND EXISTS (SELECT 1 FROM sprm_pr_line sprl LEFT JOIN sodr_po_line spl ON sprl.pr_line_id = spl.pr_line_id WHERE spl.po_line_id = srto.from_po_line_id and sprl.accepter_user_id = #{customUserDetails.userId})
and srtl.rcv_trx_line_id in
#{item}
and EXISTS (SELECT 1 FROM hpfm_company hc JOIN iam_user su WHERE hc.tenant_id = su.organization_id AND su.id = #{customUserDetails.userId} AND hc.company_num = sah.carriers_code)
and sph.display_po_num like #{displayPoNumLike}
and sph.po_num like #{poNumLike}
AND srtl.trx_date >= #{sinvRcvTrxQueryDTO.trxDateStart}
AND srtl.trx_date < #{sinvRcvTrxQueryDTO.trxDateEnd}
AND spll.need_by_date >= #{sinvRcvTrxQueryDTO.needByDateStart}
AND spll.need_by_date <= #{sinvRcvTrxQueryDTO.needByDateEnd}
AND spll.promise_delivery_date >= #{sinvRcvTrxQueryDTO.promiseDeliveryDateStart}
AND spll.promise_delivery_date < #{sinvRcvTrxQueryDTO.promiseDeliveryDateEnd}
order by rcv_trx_line_id desc
