mapper.select:
select
count(*)
from
ssta_bill_line ss
where ss.source_settle_line_num = #{sinv.lineNumber}
and ss.source_settle_num = #{sinv.acceptanceNumber}
and ss.tenant_id = #{sinv.tenantId}
and ss.canceled_flag = '0'

mapper.update:
update  ssta_settle ss
    set ss.quantity = #{inventoryQuantity}
where ss.tenant_id = #{tenantId}
and ss.source_settle_num = #{num}
and ss.source_settle_line_num = #{lineNum}
