mapper.select:
select 'SRM' as source_code, sic.category_code as item_code, srpl.item_name as item_name, srpl.uom_id as primary_uom_id, srpl.category_id as category_id, srpl.po_line_id as po_line_id, srpl.product_num as product_num, srpl.specifications as specifications, srpl.model as model FROM sodr_po_line srpl left join smdm_item_category sic on srpl.category_id = sic.category_id WHERE srpl.po_line_id IN
#{poLineId}
and srpl.item_id is null and sic.tenant_id = #{tenantId}

select 'SRM' as source_code, si.item_id as item_id, si.item_code as item_code, si.item_name as item_name, spl.po_line_id as po_line_id, spl.category_id as category_id, spl.tenant_id as tenant_id from smdm_item si left join sodr_po_line spl on si.attribute_bigint1 = spl.po_line_id and si.tenant_id = spl.tenant_id where si.attribute_bigint1 in
#{poLineId}
and si.tenant_id = #{tenantId}

update.foreach:
update sodr_po_line


        item_id = #{poLine.itemId},


        item_code = #{poLine.itemCode}


where po_line_id = #{poLine.poLineId} and tenant_id = #{poLine.tenantId}
