mapper.select:
SELECT
    count(0)
FROM
    sslm_supplier_basic ssb
WHERE ssb.tenant_id = #{tenantId}
AND ssb.unified_social_code = #{unifiedSocialCode}

 SELECT
     count(0)
 FROM
     smdm_item_category sic
 WHERE sic.tenant_id = #{tenantId}
AND sic.category_code = #{skuCategoryCode}

SELECT
    count(0)
FROM
    smpc_sku ss
WHERE ss.tenant_id = #{tenantId}
  AND ss.sku_code = #{skuNo}
